import logging
import minitel.tc as tc
from minitel.apps import BaseApp, register, appForCode
from minitel.assets import asset
from bisect import bisect
import random


@register("horoscope", ["astro"])
class HoroscopeApp(BaseApp):
    def interact(self):
        self.m.sendfile(asset("fesste/HOROSCOPE.vdt"))
        self.m.pos(24, 31)
        self.m._write(tc.ESC + tc.tVideoInverseStart)
        self.m.print("ENVOI")
        signe = self.m.addInputField(24, 28, 2, "")
        self.m.keyHandlers[tc.kEnvoi] = tc.Break
        self.m.handleInputsUntilBreak()
        self.show_results()
    
    def show_results(self):
        self.m.reset()
        fortune = random.choice(self.fortunes)
        self.m.clear()
        self.m.pos(3, 1)
        self.m._write(tc.tFgColor(tc.clYellow))
        self.m.print(fortune)
        self.m.keyHandlers[tc.kEnvoi] = tc.Break
        self.m.handleInputsUntilBreak()

